var db = require("../models");

module.exports = {
  create: function create(req, res) {
    db.Recipe.create(req.body).then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.json(err);
    });
  },
  findAll: function findAll(req, res) {
    db.Recipe.find().then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.status(422).json(err);
    });
  },
  findById: function findById(req, res) {
    db.Recipe.findById(req.params.id).then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.status(422).json(err);
    });
  },
  remove: function remove(req, res) {
    db.Recipe.findById({
      _id: req.params.id
    }).then(function (dbModel) {
      return dbModel.remove();
    }).then(function (dbModel) {
      return res.json(dbModel);
    }).catch(function (err) {
      return res.status(422).json(err);
    });
  }
};
