import axios from "axios";

export default {
    createRecipe: function (recipeData){
        return axios.post("/api/recipe", recipeData);
    },
    findRecipe: function(recipeData){
        return axios.get("/api/user/?recipe="+ recipeData.recipeName );
    }
};
