import React, { Component } from "react";
import API from "../utils/API";

class Recipe extends Component {
    state= {
        recipes: [],
        recipeName: "",
        ingredients: [],
        directions: ""
    }

    componentDidMount() {
      this.loadRecipe();
    }

    loadRecipe = () => {
      API.getRecipe()
        .then(res =>
          this.setState({ recipe: res.data, recipeName: "", ingredients: [], directions: "" })
        )
        .catch(err => console.log(err));
    };

    deleteRecipe = id => {
      API.deleteRecipe(id)
        .then(res => this.loadRecipe())
        .catch(err => console.log(err));
    };

    handleInputChange = event => {
      const { name, value } = event.target;
      this.setState({
        [name]: value
      });
    };

    handleFormSubmit = event => {
      event.preventDefault();
      if (this.state.title && this.state.author) {
        API.saveRecipe({
          recipeName: this.state.recipeName,
          ingredients: this.state.ingredients,
          directions: this.state.directions
        })
          .then(res => this.loadRecipe())
          .catch(err => console.log(err));
      }
    };

    render() {
      return (
          <>
            <h1>Recipes</h1>
                <form>
                <inp
                    value={this.state.recipeName}
                    onChange={this.handleInputChange}
                    name="recipeName"
                    placeholder="Recipe Name (required)"
                />
                <Input
                    value={this.state.ingredients}
                    onChange={this.handleInputChange}
                    name="ingredients"
                    placeholder="Ingredients (required)"
                />
                <TextArea
                    value={this.state.directions}
                    onChange={this.handleInputChange}
                    name="directions"
                    placeholder="Directions (Optional)"
                />
                <FormBtn
                    disabled={!(this.state.ingredients && this.state.recipeName)}
                    onClick={this.handleFormSubmit}
                >
                    Submit Recipe
                </FormBtn>
                </form>
                <h1>Recipe List</h1>
                {this.state.recipe.length ? (
              <div>
                {this.state.recipe.map(recipe => (
                <>
                  <p>
                      <strong>
                        {recipe.title} by {recipe.author}
                      </strong>
                  </p>
                  <p>
                      {recipe.ingredients}
                  </p>
                  <p>
                      {recipe.directions}
                  </p>
                  </>
                ))}
              </div>
            ) : (
              <h3>No Results to Display</h3>
            )}

        </>
      )}
}
  export default Recipe;
